import { useState } from "react";
import './App.css';

function App() {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [isbn, setIsbn] = useState("");
  const [books, setBook] = useState([]);
  const [isEditIndex, setIsEditIndex] = useState(null);

  const sumbitBookFormHandler = (event) => {
    event.preventDefault();
    if (!title || !author || !isbn) {
      alert("Please fill out the fields");
      return;
    }
    else {
      console.log(title)
      setBook([...books, { title, author, isbn }]);
      // console.log(setBook,'book');
      setTitle("");
      setAuthor("");
      setIsbn("");
    }
  }
  const editBtnHandler = (event,index) => {
    event.preventDefault();
    setIsEditIndex(index);
    console.log(isEditIndex,"isEditIndex");
  }
  return (
    <div className="container">
      <h1>Add Book</h1>
      <form id="book-form" onSubmit={sumbitBookFormHandler}>
        <div>
          <label>Title</label>
          <input
            type="text"
            id="title"
            className="u-full-width"
            value={title}
            onChange={(event) => setTitle(event.target.value)}
          />
        </div>
        <div>
          <label>Author</label>
          <input
            type="text"
            id="author"
            className="u-full-width"
            value={author}
            onChange={(event) => setAuthor(event.target.value)}
          />
        </div>
        <div>
          <label>ISBN#</label>
          <input
            type="number"
            id="isbn"
            className="u-full-width"
            value={isbn}
            onChange={(event) => setIsbn(event.target.value)}
          />
        </div>
        <div>
          <input
            type="submit"
            className="u-full-width"
          />
        </div>
      </form>
      <table className="u-full-width">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>ISBN</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody id="book-list">
          {books.map((singleBook, index) => {
            return (
              <tr key={index}>
                <td>{singleBook.title}</td>
                <td>{singleBook.author}</td>
                <td>{singleBook.isbn}</td>
                <td>
                  <button onClick={(event) => editBtnHandler(event, index)}>
                    Edit
                  </button>
                </td>
                <td>
                  <a
                    href="#"
                    className="delete"
                  >
                    X
                  </a>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default App;
