import {useState} from "react";
import './App.css';

function App() {
  const [getTitle,setTitle] = useState("");
  const [getAuthor,setAuthor] = useState("");
  const [getIsbn,setIsbn] = useState("");
  const [book,setBook] = useState([]);

  const sumbitBookFormHandler = (event) => {
    if(!getTitle || !getAuthor || !getIsbn){
      alert("Please fill out the fields");
      return;
    }
    else{
      setBook([...book,getTitle,getAuthor,getIsbn]);
      console.log(setBook,'book');
      setTitle("");
      setAuthor("");
      setIsbn("");
    }
  }
  return (
    <div className="container">
      <h1>Add Book</h1>
      <form id="book-form" onSubmit={sumbitBookFormHandler}>
        <div>
          <label>Title</label>
          <input
            type="text"
            id="title"
            className="u-full-width"
            value={getTitle}
            onChange={(event)=>setTitle(event.target.value)}
          />
        </div>
        <div>
          <label>Author</label>
          <input
            type="text"
            id="author"
            className="u-full-width"
            value={getAuthor}
            onChange={(event)=>setAuthor(event.target.value)}
          />
        </div>
        <div>
          <label>ISBN#</label>
          <input
            type="number"
            id="isbn"
            className="u-full-width"
            value={getIsbn}
            onChange={(event)=>setIsbn(event.target.value)}
          />
        </div>
        <div>
          <input
            type="submit"
            className="u-full-width"
          />
        </div>
      </form>
      <table className="u-full-width">
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>ISBN</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody id="book-list">
          {book.map((singleBook,index)=>{
            <tr key={index}>
            <td>{singleBook.getTitle}</td>
            <td>{singleBook.getAuthor}</td>
            <td>{singleBook.getIsbn}</td>
            <td>
              <button>
                Edit
              </button>
            </td>
            <td>
              <a
                href="#"
                className="delete"
              >
                X
              </a>
            </td>
          </tr>
          })}
        </tbody>
      </table>
    </div>
  );
}

export default App;
